mapper.insert:
insert into tag(
tag_num,
board_num,
tag
)
values(
tag_seq.nextval,
#{boardNum},
#{tag}
)

mapper.update:
update member_act_cnt 
set board_write_cnt = board_write_cnt+ 1 
where member_num = #{memberNum}

update board
set 
	tripdate = #{tripdate},
	place= #{place},
	content= #{content}
where board_num = #{boardNum}
