mapper.insert:
INSERT INTO MEMBER (
    ID, PASSWORD, NICKNAME, EMAIL, BIRTH_YEAR, BIRTH, GENDER) 
    VALUES (
    #{id}, #{password}, #{nickname}, #{email}, #{birthyear}, #{birth}, #{gender}
)

mapper.update:
UPDATE MEMBER
SET
    NICKNAME = #{nickname}  
    , BIRTH_YEAR = #{birthyear}
	, BIRTH = #{birth}
WHERE ID = #{id}

UPDATE MEMBER
SET
	PASSWORD = #{password}

	WHERE ID = #{id}
